import {Link} from "react-router-dom";

import '../../../styles/product-card.css'

const ProductCard = (props) => {
    const {id, image01, title, price} = props.item_value
    return (
        <div className="product_prof">
            <div className="product_item">
                <div className="product_img">
                    <img src={image01} alt="product img" className="w-100"/>
                </div>
                <div className="product_content">
                    <h5><Link to={`/foods/${id}`}>{title}</Link></h5>
                </div>
                <div className="d-flex center align-items-center justify-content-between">
                    <span className="product_price">{price}.00 ₽</span>
                    <button className="addToCart_btn">Добавить в корзину</button>
                </div>
            </div>
        </div>

    );
}

export default ProductCard;
