import {configureStore} from "@reduxjs/toolkit";

import cartSlice from "./shopping-cart/cartSlice";

const store = configureStore({
    // храним состояние cartSlice.reducers в store
    reducer:{
        cart: cartSlice,
    }

})

export default  store;
