import {Container} from "reactstrap";
import logo from './../../assets/images/logo.png'
import {NavLink, Link} from "react-router-dom";


const nav__link = [
    {
        display: 'Home',
        path: '/home'
    },
    {
        display: 'Foods',
        path: '/foods'
    },
    {
        display: 'Cart',
        path: '/cart'
    },
    {
        display: 'Contact',
        path: '/contact'
    },

];

const Header = () => {
    return (
        <header className="header">
            <Container>
                <div className="nav_wrapper">
                    <div className="logo">
                        <img src={logo} alt='logo'/>
                        <h5>Пицца-огонь!</h5>
                    </div>

                    {/*======Меню======*/}

                    <div className="navigation">
                        <div className="menu">
                            {
                                nav__link.map((item, index) => (
                                    <NavLink to={item.path} key={index}>{item.display}</NavLink>
                                ))
                            }

                        </div>
                    </div>

                    {/* значки навигации вправо*/}

                    <div className="nav_right">
                        <span className="cart_icon">
                            <i className="ri-shopping-basket-line"></i>\
                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M12.0049 2C15.3186 2 18.0049 4.68629 18.0049 8V9H22.0049V11H20.8379L20.0813 20.083C20.0381 20.6013 19.6048 21 19.0847 21H4.92502C4.40493 21 3.97166 20.6013 3.92847 20.083L3.17088 11H2.00488V9H6.00488V8C6.00488 4.68629 8.69117 2 12.0049 2ZM18.8309 11H5.17788L5.84488 19H18.1639L18.8309 11ZM13.0049 13V17H11.0049V13H13.0049ZM9.00488 13V17H7.00488V13H9.00488ZM17.0049 13V17H15.0049V13H17.0049ZM12.0049 4C9.86269 4 8.1138 5.68397 8.00978 7.80036L8.00488 8V9H16.0049V8C16.0049 5.8578 14.3209 4.10892 12.2045 4.0049L12.0049 4Z"></path></svg>
                            <span className="cart_badge">2</span>
                        </span>

                        <span className="user">
                        <Link to='/login'><i className="ri-user-line"></i> </Link>
                        </span>

                        <span className="module_menu">
                           <i class="ri-shopping-basket-line"></i>
                        </span>
                    </div>


                </div>
            </Container>
        </header>
    );
}

export default Header;
