import {Container} from "reactstrap";
import logo from './../../assets/images/logo.png'
import shopping_basket_line from './../../assets/images/shopping-basket-line.png'
import user_fill from './../../assets/images/user-fill.png'
import menu_line from './../../assets/images/menu-line.png'
import {NavLink, Link} from "react-router-dom";


const nav__link = [
    {
        display: 'Home',
        path: '/home'
    },
    {
        display: 'Foods',
        path: '/foods'
    },
    {
        display: 'Cart',
        path: '/cart'
    },
    {
        display: 'Contact',
        path: '/contact'
    },

];

const Header = () => {
    return (
        <header className="header">
            <Container>
                <div className="nav_wrapper d-flex align-items-center justify-content-between">
                    <div className="logo">
                        <img src={logo} alt='logo'/>
                        <h5>Пицца-огонь!</h5>
                    </div>

                    {/*======Меню======*/}

                    <div className="navigation">
                        <div className="menu">
                            {
                                nav__link.map((item, index) => (
                                    <NavLink to={item.path} key={index}>{item.display}</NavLink>
                                ))
                            }

                        </div>
                    </div>

                    {/* значок навигации вправо*/}
                    <div className="nav_right">
                        <span className="cart_icon">
                           <i className="ri-shopping-basket-line"> <img src={shopping_basket_line} alt="shopping_basket"/></i>
                            <span className="cart_badge">2</span>
                        </span>

                        <span className="user">
                            <Link to='/login'> <i className="ri-user-fill"></i> <img src={user_fill} alt='user'/>  </Link>
                        </span>
                        <span className="module_menu">
                            <i className="ri-menu-line"> <img src={menu_line} alt='menu'/> </i>
                        </span>
                    </div>
                </div>
            </Container>
        </header>
    );
}

export default Header;
