import {ListGroupItem} from "reactstrap";
import prodImg from "../../../assets/images/product_01.1.jpg"
import closeImg from "../../../assets/images/close-circle-fill.png"
import plusImg from "../../../assets/images/add-line.png"
import minusImg from "../../../assets/images/subtract-line.png"

const CartItem = () => {
    return (
        <ListGroupItem>
            <div className="cart_item_info">
                <img src={prodImg} alt="product img"/>
                <div className="cart_product_info">
                    <h6>Бургер</h6>
                    <p>2x <span>24.00</span></p>
                        <span><i className="ri-add-circle-line"> <img src={plusImg} alt="plus img"/></i></span>
                        <span>2</span>
                        <span><i className="ri-subtract-line"><img src={minusImg} alt="minus img"/></i></span>
                </div>
                <span> <i className="ri-close-circle-fill"> <img src={closeImg} alt="close"/></i></span>
            </div>
        </ListGroupItem>
    );
}

export default CartItem;
