import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import {Container, Row, Col} from "reactstrap";

import searchImg from "../assets/images/search-2-line.svg"

import products from "../assets/product-data/products";
import ProductCard from "../components/UI/product-card/ProductCard";

import "../styles/all-foods.css"
import {useState} from "react";


const AllFoods = () => {

    const [searchTerm, setSearchTerm] = useState('');
    const [productData, setProductData] = useState(products)

    return (
        <Helmet title='All-Foods'>
            <CommonSection title=' Все продукты'/>

            <section>
                <Container>
                    <Row>
                        <Col lg="6" md="6" sm="6">
                            <div className="search_widget d-flex align-items-center justify-content-between w-50 ">
                                <input type="text" maxLength="35" placeholder="Введите название продукта"
                                       value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)}/>
                                <span className="search-icon"><img src={searchImg} alt="searchImg"
                                           style={{width: "25px", height: "25px"}}/></span>
                            </div>
                        </Col>
                        <Col lg="6" md="6" sm="6">
                            <div className="sorting_widget text-end">
                                <select defaultValue={'DEFAULT'}>
                                    <option value="DEFAULT" disabled>Сортировать по...</option>
                                    <option value="high-price">Возрастанию</option>
                                    <option value="low-price">Убыванию</option>
                                </select>
                            </div>
                        </Col>
                        {
                            productData.filter((item) => {
                                if (searchTerm.value === "")
                                    return item;
                                if (item.title.toLowerCase().includes(searchTerm.toLowerCase()))
                                    return item;
                            }).map((item, index) => (
                                <Col lg="3" md="4" sm="6" xs="6" className="mt-5" key={index}>
                                    <ProductCard item_value={item} key={item.id}/>
                                </Col>
                            ))
                        }
                    </Row>
                </Container>
            </section>
        </Helmet>
    );
}

export default AllFoods;
