import {ListGroup} from "reactstrap";
import {Link} from "react-router-dom";

import CartItem from "./CartItem";

import closeImg from "../../../assets/images/close-circle-fill.svg"

import "../../../styles/shopping-cart.css"
import {useDispatch} from "react-redux";
import {cartUiActions} from "../../../store/shopping-cart/cartUiSlice";

const Carts = () => {
    debugger;
    const dispatch = useDispatch();
    debugger;
    const toggleCartOut = ()=>{
        dispatch(cartUiActions.toggle())
    }

    return (
        <div className="cart_container">
            <ListGroup className="cart p-1">
                <div className="cart_close">
                    <span onClick={toggleCartOut}> <i className="ri-close-circle-fill"> <img className="close_circle_fill" src={closeImg} alt="close" style={{width: "25px", height: "30px"}}/></i></span>
                </div>
                <div className="cart_item_list">
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>
                    <CartItem/>

                </div>
                <div className="cart_bottom d-flex justify-content-between align-items-center">
                    <h6>Сумма: <span>123₽</span></h6>
                    <button><Link to="/checkout">Проверить продукты</Link></button>
                </div>
            </ListGroup>
        </div>
    );
}
export default Carts;
