import {Container} from "reactstrap";
import {NavLink, Link} from "react-router-dom";
import {useEffect, useRef} from "react";

import logo from './../../assets/images/logo.png'
import shopping_basket_line from './../../assets/images/shopping-basket-line.png'
import user_fill from './../../assets/images/user-fill.png'
import menu_line from './../../assets/images/menu-line.png'

import '../../styles/header.css'


const nav_link = [
    {
        display: 'Главная',
        path: '/home'
    },
    {
        display: 'Продукты',
        path: '/foods'
    },
    {
        display: 'Корзина',
        path: '/cart'
    },
    {
        display: 'Контакты',
        path: '/contact'
    },

];

const Header = () => {

    const menuRef = useRef(null);
    const headerRef = useRef(null);
    const toggleMenu = () => menuRef.current.classList.toggle('show_menu');

    useEffect(() => {
        window.addEventListener('scroll',()=>{
            if(document.body.scrollTop > 1 || document.documentElement.scrollTop > 100)
            {
                headerRef.current.classList.add('header_shrink');
            }
            else
            {
                headerRef.current.classList.remove('header_shrink');
            }
        })

    },[])

    return (
        <header className="header" ref = {headerRef}>
            <Container>
                <div className="nav_wrapper d-flex align-items-center justify-content-between">
                    <div className="logo">
                        <img src={logo} alt='logo'/>
                        <h5>Mama Leahs</h5>
                    </div>

                    {/*======Меню======*/}

                    <div className="navigation" ref={menuRef} onClick={toggleMenu}>
                        <div className="menu d-flex align-items-center gap-5">
                            {
                                nav_link.map((item, index) => (
                                    <NavLink
                                        className={navClass => navClass.isActive ? 'active_menu' : ''}
                                        to={item.path} key={index}> {item.display}</NavLink>
                                ))
                            }
                        </div>
                    </div>

                    {/* значки навигации*/}

                    <div className="nav_right d-flex align-items-center gap-4 ">
                        <span className="cart_icon">
                           <i className="ri-shopping-basket-line"> <img src={shopping_basket_line}
                                                                        alt="shopping_basket"/></i>
                            <span className="cart_badge">2</span>
                        </span>

                        <span className="user">
                            <Link to='/login'> <i className="ri-user-fill"><img src={user_fill}
                                                                                alt='user'/></i>   </Link>
                        </span>
                        <span className="mobile_menu" onClick={toggleMenu}>
                            <i className="ri-menu-line"> <img src={menu_line} alt='menu'/> </i>
                        </span>
                    </div>
                </div>
            </Container>
        </header>
    );
}

export default Header;
