import CommonSection from "../components/UI/common-section/CommonSection";
import Helmet from "../components/Helmet/Helmet";

import {Container, Row, Col} from "reactstrap";

import searchImg from "../assets/images/search-2-line.svg"

import products from "../assets/product-data/products";
import ProductCard from "../components/UI/product-card/ProductCard";

import "../styles/all-foods.css"

function limitInput( k, obj ) {
    switch( k ){
        case 'ru':
            obj.value = obj.value.replace(/[^а-яА-ЯёЁ0-9 -]/ig,'');
            break;
    }
}



const AllFoods = () => {
    return (
        <Helmet title='All-Foods'>
            <CommonSection title=' Все продукты'/>

            <section>
                <Container>
                    <Row>
                        <Col lg="6" md="6" sm="6">
                            <div className="search_widget d-flex align-items-center justify-content-between w-50 ">
                                <input type="text" onClick={()=>limitInput( 'ru', this )} maxLength="30"   placeholder="Введите название"/>
                                <span><img src={searchImg} alt="searchImg" style={{width: "25px", height: "25px"}}/></span>
                            </div>
                        </Col>
                        <Col lg="6" md="6" sm="6">
                            <div className="sorting_widger text-end">
                                <select defaultValue={'DEFAULT'}>
                                    <option value="DEFAULT" disabled>Выберите категорию продукта</option>
                                    <option value="high-price">Дороже</option>
                                    <option value="low-price">Дешевле</option>
                                </select>
                            </div>
                        </Col>
                        {
                            products.map((item, index) => (
                                <Col lg="3" md="4" sm="6" xs="6" className="mt-5" key={index}>
                                    <ProductCard item_value={item} key={item.id}/>
                                </Col>
                            ))
                        }
                    </Row>
                </Container>
            </section>
        </Helmet>
    );
}

export default AllFoods;
