import CommonSection from "../components/UI/common-section/CommonSection";
import {Col, Container, Row} from "reactstrap";

import deleteIcon from '../assets/images/delete-bin-5-line.svg'

import '../styles/cart-pages.css'
import {useDispatch, useSelector} from "react-redux";
import {cartAction} from "../store/shopping-cart/cartSlice";

const Cart = (props) => {

    const cartItems = useSelector(state => state.cart.cartItems)
    const dispatch = useDispatch();

    const deleteProduct = (props)=>{
        dispatch(cartAction.deleteItem(props.item.id))
    }
    return (
        <>
            <CommonSection title="Корзина"/>
            <section>
                <Container>
                    <Row>
                        <Col lg="12">
                            {
                                cartItems.length === 0 ? <h5 className="text-center">Вы не добавили товары</h5> :
                                    <table>
                                        <thead>
                                        <tr>
                                            <th>Фото</th>
                                            <th>Название продукта</th>
                                            <th>Стоимость</th>
                                            <th>Количетсво</th>
                                            <th>Удалить</th>
                                        </tr>
                                        </thead>
                                        <tbody className="text-center">
                                        {
                                            cartItems.map((item)=>(
                                                <Tr item_value_cartItems = {item} key = {item.id} />
                                            ))
                                        }

                                        </tbody>
                                    </table>
                            }
                        </Col>
                    </Row>
                </Container>
            </section>
        </>
    );
}

const Tr = (props) => {

    const {title, quantity, price, image01} = props.item_value_cartItems

    return (
        <tr>
            <td><img src={image01} alt="product cartItems" style={{width: "100px", height: "100px"}} /></td>
            <td>{title}</td>
            <td>{price}</td>
            <td>{quantity}</td>
            <td>
                <img src={deleteIcon} alt="deleteIcon"
                     style={{width: "20px", height: "20px"}}/>
            </td>
        </tr>
    );

}

export default Cart;
