import { configureStore } from "@reduxjs/toolkit";
import cartReducer from "./path-to-your-cart-slice"; // Import your cart slice reducer

const store = configureStore({
    reducer: {
        cart: cartReducer, // Make sure you use the correct key 'cart' for your slice
        // Add other reducers as needed
    },
});

export default store;
